INSERT INTO dbo.OEA_Subseccion (SubseccionId, SeccionId, Nombre, Descripcion, Orden, Activo, CreadoPor, CreadoEn) VALUES (31, 8, N'Subseccion 8.1', N'', 31, 1, 1, GETDATE());
INSERT INTO dbo.OEA_Requisito (RequisitoId, SubseccionId, Codigo, Descripcion, Orden, Activo, CreadoPor, CreadoEn) VALUES (1, 1, N'1.1', N'La empresa o actor de la cadena logística internacional de mercancías, debe establecer un procedimiento documentado que defina la metodología para la selección y revisión periódica de sus asociados de negocio, conforme a su capacidad para cumplir los requisitos de seguridad relacionados a la cadena logística de mercancías que incluya como mínimo:
INSERT INTO dbo.OEA_Requisito (RequisitoId, SubseccionId, Codigo, Descripcion, Orden, Activo, CreadoPor, CreadoEn) VALUES (2, 1, N'1.2', N'Para verificar el cumplimiento de los acuerdos de seguridad de los asociados del negocio críticos, debe realizar auditorías de segunda parte u otro método de verificación formal; la frecuencia depende del nivel de criticidad. En caso de que no sea viable formalizar un acuerdo de seguridad, la empresa debe establecer controles operacionales que permitan gestionar los riesgos relacionados.', 2, 1, 1, GETDATE());
INSERT INTO dbo.OEA_Requisito (RequisitoId, SubseccionId, Codigo, Descripcion, Orden, Activo, CreadoPor, CreadoEn) VALUES (3, 1, N'1.3', N'Se debe gestionar las acciones correctivas correspondientes para asegurar el seguimiento a los resultados de dicha verificación.', 3, 1, 1, GETDATE());
INSERT INTO dbo.OEA_Requisito (RequisitoId, SubseccionId, Codigo, Descripcion, Orden, Activo, CreadoPor, CreadoEn) VALUES (4, 1, N'1.4', N'Debe contar con una lista actualizada de los asociados de negocio', 4, 1, 1, GETDATE());
INSERT INTO dbo.OEA_Requisito (RequisitoId, SubseccionId, Codigo, Descripcion, Orden, Activo, CreadoPor, CreadoEn) VALUES (5, 1, N'1.5', N'Debe establecer el nivel de criticidad de los asociados del
INSERT INTO dbo.OEA_Requisito (RequisitoId, SubseccionId, Codigo, Descripcion, Orden, Activo, CreadoPor, CreadoEn) VALUES (6, 1, N'1.6', N'La empresa o actor de la cadena logística internacional de mercancías, debe contar con un esquema basado en matrices de riesgo en el cual se establezcan medidas para la selección de proveedores y clientes, en el cual una vez formalizada la relación comercial en el caso de asociados de negocios se celebre un contrato u otro método de verificación formal. (Para efectos de las medidas de selección de asociados de negocios: verificación de antecedentes, otros clientes que atiende, registros, evaluación de riesgo, en el caso de proveedores comprobar que estén debidamente registrados y solventes ante la SAT).             Según el proceso de evaluación de riesgos, los socios comerciales, deben actualizar las autoevaluaciones de seguridad periódicamente, basado en causa o según lo exijan las circunstancias.', 6, 1, 1, GETDATE());
INSERT INTO dbo.OEA_Requisito (RequisitoId, SubseccionId, Codigo, Descripcion, Orden, Activo, CreadoPor, CreadoEn) VALUES (7, 1, N'1.7', N'La empresa o actor de la cadena logística internacional de mercancías, debe contar con la metodología que la relación comercial con sus clientes es directa y está formalizada mediante contratos de servicios u otro método de verificación formal (Cotización con firma de ambas partes) Para efectos del contrato u otro método de verificación formal debe contemplar: cláusulas de responsabilidad, confidencialidad y seguridad en los contratos, entre otros.', 7, 1, 1, GETDATE());
INSERT INTO dbo.OEA_Requisito (RequisitoId, SubseccionId, Codigo, Descripcion, Orden, Activo, CreadoPor, CreadoEn) VALUES (8, 1, N'1.8', N'Para la prevención del lavado de activos y financiación del terrorismo, el procedimiento para la selección de los asociados de negocio debe incluir criterios de prevención tales como:
INSERT INTO dbo.OEA_Requisito (RequisitoId, SubseccionId, Codigo, Descripcion, Orden, Activo, CreadoPor, CreadoEn) VALUES (9, 2, N'2.1.1', N'LA EMPRESA O ACTOR DE LA CADENA LOGÍSTICA INTERNACIONAL DE MERCANCÍAS, DEBE contar con el procedimiento o instructivo que establezca los pasos para la contratación del personal.', 9, 1, 1, GETDATE());
INSERT INTO dbo.OEA_Requisito (RequisitoId, SubseccionId, Codigo, Descripcion, Orden, Activo, CreadoPor, CreadoEn) VALUES (10, 2, N'2.1.2', N'La empresa o actor de la cadena logística internacional de mercancías, debe contar con un procedimiento documentado, conforme a la legislación, que regule las siguientes actividades de efectuar la investigación y comprobación de la información proporcionada por el empleado. Verificar antes de la contratación:
INSERT INTO dbo.OEA_Requisito (RequisitoId, SubseccionId, Codigo, Descripcion, Orden, Activo, CreadoPor, CreadoEn) VALUES (11, 2, N'2.1.3', N'La empresa o actor de la cadena logística internacional de mercancías, debe contar con un perfil de puestos (Que incluya por lo menos la escolaridad y áreas de conocimiento, experiencia laboral, los requerimientos o
INSERT INTO dbo.OEA_Requisito (RequisitoId, SubseccionId, Codigo, Descripcion, Orden, Activo, CreadoPor, CreadoEn) VALUES (12, 2, N'2.1.4', N'La empresa o actor de la cadena logística internacional de mercancías, debe contar con el procedimiento o instructivo que establezca como realiza pruebas de detección de consumo de sustancias ilícitas (drogas y alcohol, entre otras) a los candidatos a ocupar áreas críticas.', 12, 1, 1, GETDATE());
INSERT INTO dbo.OEA_Requisito (RequisitoId, SubseccionId, Codigo, Descripcion, Orden, Activo, CreadoPor, CreadoEn) VALUES (13, 2, N'2.1.5', N'La empresa o actor de la cadena logística internacional de mercancías, debe contar con el procedimiento o instructivo que establezca como realiza visitas domiciliarias y pruebas de confiabilidad a los candidatos a ocupar áreas críticas.', 13, 1, 1, GETDATE());
INSERT INTO dbo.OEA_Requisito (RequisitoId, SubseccionId, Codigo, Descripcion, Orden, Activo, CreadoPor, CreadoEn) VALUES (14, 2, N'2.1.6', N'Incluye dicho procedimiento la Inducción y entrenamiento, consecuencias por incumplimiento de normas y procedimientos en materia de seguridad, registros de afiliación, entre otros).', 14, 1, 1, GETDATE());
INSERT INTO dbo.OEA_Requisito (RequisitoId, SubseccionId, Codigo, Descripcion, Orden, Activo, CreadoPor, CreadoEn) VALUES (15, 3, N'2.2.1', N'La empresa o actor de la cadena logística internacional de mercancías, debe contar con el procedimiento o instructivo que establezca que regule la emisión, remoción y cambio de las identificaciones de accesos físicos e informáticos otorgadas al personal de la empresa (Debe indicar la forma de habilitar e inhabilitar el acceso de las personas a las instalaciones, cuando expire o caduque y en caso de abandono, suspensión, renuncia o despido).', 15, 1, 1, GETDATE());
INSERT INTO dbo.OEA_Requisito (RequisitoId, SubseccionId, Codigo, Descripcion, Orden, Activo, CreadoPor, CreadoEn) VALUES (16, 3, N'2.2.2', N'El personal de la empresa debe contar con un gafete con fotografía reciente y número de identificación', 16, 1, 1, GETDATE());
INSERT INTO dbo.OEA_Requisito (RequisitoId, SubseccionId, Codigo, Descripcion, Orden, Activo, CreadoPor, CreadoEn) VALUES (17, 3, N'2.2.3', N'El personal de la empresa debe utilizar el gafete de identificación en un lugar visible y en todo momento de acuerdo con las políticas de seguridad industrial aplicables', 17, 1, 1, GETDATE());
INSERT INTO dbo.OEA_Requisito (RequisitoId, SubseccionId, Codigo, Descripcion, Orden, Activo, CreadoPor, CreadoEn) VALUES (18, 3, N'2.2.4', N'La empresa o actor de la cadena logística internacional de mercancías, debe contar con el expediente actualizado de cada uno de los empleados. (Puede incluir por lo menos los documentados siguientes: hoja de vida, antecedentes penales y policiacos, cartas de recomendación con historial laboral, cartas de recomendación personal, fotocopia de constancia de capacitaciones recibidas, Estudio Socioeconómico, Pruebas de Alcohol y sustancias ilícitas, entre otros y considerando la legislación laboral de nuestro país.)', 18, 1, 1, GETDATE());
INSERT INTO dbo.OEA_Requisito (RequisitoId, SubseccionId, Codigo, Descripcion, Orden, Activo, CreadoPor, CreadoEn) VALUES (19, 3, N'2.2.5', N'La empresa o actor de la cadena logística internacional de mercancías, debe contar con un programa de visita al domicilio del personal que labora en áreas críticas, mínimo cada dos años cumpliendo con los requisitos legales y reglamentarios del país o propios de la empresa u organización.', 19, 1, 1, GETDATE());
INSERT INTO dbo.OEA_Requisito (RequisitoId, SubseccionId, Codigo, Descripcion, Orden, Activo, CreadoPor, CreadoEn) VALUES (20, 3, N'2.2.6', N'La empresa o actor de la cadena logística internacional de mercancías, puede llevar control de las actualizaciones de datos personales realizadas por los empleados (El fin es realizar una comparación de sus datos actuales en relación con los anteriores y sacar conclusiones que permitan saber que el empleado continúa siendo seguro para la empresa).', 20, 1, 1, GETDATE());
INSERT INTO dbo.OEA_Requisito (RequisitoId, SubseccionId, Codigo, Descripcion, Orden, Activo, CreadoPor, CreadoEn) VALUES (21, 3, N'2.2.7', N'La empresa o actor de la cadena logística internacional de mercancías, debe aplicar pruebas de confiabilidad al personal que ocupa los cargos críticos de forma aleatoria, máximo cada dos años y cuando se presente sospechas.', 21, 1, 1, GETDATE());
INSERT INTO dbo.OEA_Requisito (RequisitoId, SubseccionId, Codigo, Descripcion, Orden, Activo, CreadoPor, CreadoEn) VALUES (22, 3, N'2.2.8', N'La empresa o actor de la cadena logística internacional de mercancías, debe verificar los antecedentes penales y policiacos del personal que ocupa cargos críticos y establecer a criterio de la empresa la periodicidad del proceso', 22, 1, 1, GETDATE());
INSERT INTO dbo.OEA_Requisito (RequisitoId, SubseccionId, Codigo, Descripcion, Orden, Activo, CreadoPor, CreadoEn) VALUES (23, 3, N'2.2.9', N'La empresa o actor de la cadena logística internacional de mercancías, debe aplicar pruebas para detectar el consumo de alcohol y drogas ilícitas aleatoriamente basado en causa y cargos críticos, máximo cada dos años y cuando se presente sospechas', 23, 1, 1, GETDATE());
INSERT INTO dbo.OEA_Requisito (RequisitoId, SubseccionId, Codigo, Descripcion, Orden, Activo, CreadoPor, CreadoEn) VALUES (24, 3, N'2.2.10', N'La empresa o actor de la cadena logística internacional de mercancías, puede mantener un programa de prevención de adicciones', 24, 1, 1, GETDATE());
INSERT INTO dbo.OEA_Requisito (RequisitoId, SubseccionId, Codigo, Descripcion, Orden, Activo, CreadoPor, CreadoEn) VALUES (25, 3, N'2.2.11', N'La empresa o actor de la cadena logística internacional de mercancías, puede mantener un programa de prevención del riesgo de corrupción y soborno para el personal propio, subcontratado o tercerizado', 25, 1, 1, GETDATE());
INSERT INTO dbo.OEA_Requisito (RequisitoId, SubseccionId, Codigo, Descripcion, Orden, Activo, CreadoPor, CreadoEn) VALUES (26, 3, N'2.2.12', N'La empresa o actor de la cadena logística internacional de mercancías, puede contar con un procedimiento documentado u otro documento en el cual se establezcan las acciones del personal cuando se establece
INSERT INTO dbo.OEA_Requisito (RequisitoId, SubseccionId, Codigo, Descripcion, Orden, Activo, CreadoPor, CreadoEn) VALUES (27, 3, N'2.2.13', N'La empresa o actor de la cadena logística internacional de mercancías, debe contar con un procedimiento documentado o instructivo que indique la forma de proceder cuando el personal cese la relación laboral con la empresa o cambie de puesto (Cuidar la devolución de los gafetes, uniformes, la cancelación de accesos físicos e informáticos, entrega de sellos, llaves, procedimiento documentado o instructivo vinculantes con la empresa, entre otros. Todas estas devoluciones deben estar respaldadas con su respectiva solvencia).', 27, 1, 1, GETDATE());
INSERT INTO dbo.OEA_Requisito (RequisitoId, SubseccionId, Codigo, Descripcion, Orden, Activo, CreadoPor, CreadoEn) VALUES (28, 3, N'2.2.14', N'La empresa o actor de la cadena logística internacional de mercancías, puede contar con un procedimiento documentado u otro documento en el cual se establezcan las acciones del personal cuando se establece la existencia de un incidente anómalo o hecho que atente contra la seguridad (Conspiraciones internas, consumo de sustancias ilícitas y todas aquellas que pueden representar un riesgo para la seguridad de la empresa)', 28, 1, 1, GETDATE());
INSERT INTO dbo.OEA_Requisito (RequisitoId, SubseccionId, Codigo, Descripcion, Orden, Activo, CreadoPor, CreadoEn) VALUES (29, 4, N'2.3.1', N'La empresa o actor de la cadena logística internacional de mercancías, debe contar con un procedimiento documentado u otro documento en el que se registre la inducción y capacitación en materia de seguridad que
INSERT INTO dbo.OEA_Requisito (RequisitoId, SubseccionId, Codigo, Descripcion, Orden, Activo, CreadoPor, CreadoEn) VALUES (30, 4, N'2.3.2', N'La empresa o actor de la cadena logística internacional de mercancías, debe contar con programas recurrentes de sensibilización y capacitación dirigidos al personal de seguridad y el de las áreas críticas sobre
INSERT INTO dbo.OEA_Requisito (RequisitoId, SubseccionId, Codigo, Descripcion, Orden, Activo, CreadoPor, CreadoEn) VALUES (31, 4, N'2.3.3', N'La empresa o actor de la cadena logística internacional de mercancías, debe contar con un programa permanente que proporcione a sus empleados capacitación sobre el uso y manejo de equipo de seguridad e higiene industrial, así como las implicaciones por incumplimiento de las mismas.', 31, 1, 1, GETDATE());
INSERT INTO dbo.OEA_Requisito (RequisitoId, SubseccionId, Codigo, Descripcion, Orden, Activo, CreadoPor, CreadoEn) VALUES (32, 4, N'2.3.4', N'La empresa o actor de la cadena logística internacional de mercancías, debe llevar control y registro de las capacitaciones que cada empleado recibe en materia de seguridad (Deben estar registradas en el expediente de cada empleado preferentemente)', 32, 1, 1, GETDATE());
INSERT INTO dbo.OEA_Requisito (RequisitoId, SubseccionId, Codigo, Descripcion, Orden, Activo, CreadoPor, CreadoEn) VALUES (33, 4, N'2.3.5', N'La empresa o actor de la cadena logística internacional de mercancías, puede contar con un programa anual de capacitación que incluya como mínimo:
INSERT INTO dbo.OEA_Requisito (RequisitoId, SubseccionId, Codigo, Descripcion, Orden, Activo, CreadoPor, CreadoEn) VALUES (34, 5, N'3.1.1', N'Debe contar con un procedimiento documentado u otro documento en el que se establezcan las medidas para controlar entradas y salidas de la carga', 34, 1, 1, GETDATE());
INSERT INTO dbo.OEA_Requisito (RequisitoId, SubseccionId, Codigo, Descripcion, Orden, Activo, CreadoPor, CreadoEn) VALUES (35, 5, N'3.1.2', N'La empresa o actor de la cadena logística internacional de mercancías, debe contar con un procedimiento documentado u otro documento en el cual se establezcan medidas para la seguridad de las mercancías.', 35, 1, 1, GETDATE());
INSERT INTO dbo.OEA_Requisito (RequisitoId, SubseccionId, Codigo, Descripcion, Orden, Activo, CreadoPor, CreadoEn) VALUES (36, 5, N'3.1.3', N'La empresa o actor de la cadena logística internacional de mercancías, debe contar con un procedimiento documentado u otro documento que registre:                                                                                                                                          a. las actividades de manipulación de la mercancía que se realicen dentro de las instalaciones.
INSERT INTO dbo.OEA_Requisito (RequisitoId, SubseccionId, Codigo, Descripcion, Orden, Activo, CreadoPor, CreadoEn) VALUES (37, 5, N'3.1.4', N'Debe contar con un lugar adecuado, seguro y con acceso restringido en el cual se resguarden los procedimientos documentados o instructivos completos y legibles relacionados con las mercancías.', 37, 1, 1, GETDATE());
INSERT INTO dbo.OEA_Requisito (RequisitoId, SubseccionId, Codigo, Descripcion, Orden, Activo, CreadoPor, CreadoEn) VALUES (38, 5, N'3.1.5', N'El personal de las áreas de carga, descarga, estiba y manejo de montacargas utiliza uniforme o algún tipo de distintivo del resto del personal de la empresa (Chalecos, cascos, gafete de otro color, entre otros).', 38, 1, 1, GETDATE());
INSERT INTO dbo.OEA_Requisito (RequisitoId, SubseccionId, Codigo, Descripcion, Orden, Activo, CreadoPor, CreadoEn) VALUES (39, 5, N'3.1.6', N'Debe estar establecido quien es la persona responsable de la seguridad de las mercancías en las operaciones de carga y descarga en las cuales por su rol en la cadena logística le corresponde intervenir (Debe quedar registro de los participantes).', 39, 1, 1, GETDATE());
INSERT INTO dbo.OEA_Requisito (RequisitoId, SubseccionId, Codigo, Descripcion, Orden, Activo, CreadoPor, CreadoEn) VALUES (40, 5, N'3.1.7', N'Debe contar con un registro electrónico o manual por medio del cual se pueda:
INSERT INTO dbo.OEA_Requisito (RequisitoId, SubseccionId, Codigo, Descripcion, Orden, Activo, CreadoPor, CreadoEn) VALUES (41, 5, N'3.1.8', N'Debe contar con un mecanismo documentado que permita mantener trazabilidad sobre los movimientos de la carga de importación y exportación.', 41, 1, 1, GETDATE());
INSERT INTO dbo.OEA_Requisito (RequisitoId, SubseccionId, Codigo, Descripcion, Orden, Activo, CreadoPor, CreadoEn) VALUES (42, 6, N'3.2.1', N'Debe contar con un procedimiento documentado u otro documento en el que se establezcan políticas de monitoreo de la mercancía en tránsito, en la que su empresa es responsable de tramites e información de procedimientos documentados u otros documentos de soporte aduanero', 42, 1, 1, GETDATE());
INSERT INTO dbo.OEA_Requisito (RequisitoId, SubseccionId, Codigo, Descripcion, Orden, Activo, CreadoPor, CreadoEn) VALUES (43, 7, N'3.3.1', N'Debe contar con un procedimiento documentado o instructivo en el que se establezcan las medidas que se deben tomar para investigar y reportar faltantes o sobrantes de la mercancía', 43, 1, 1, GETDATE());
INSERT INTO dbo.OEA_Requisito (RequisitoId, SubseccionId, Codigo, Descripcion, Orden, Activo, CreadoPor, CreadoEn) VALUES (44, 8, N'3.4.1', N'Debe contar con un procedimiento documentado u otro documento en el que se establezcan lineamientos que permitan asegurar que el vaciado de datos de la información que se usa para la elaboración de las declaraciones de mercancías es fidedigna y correcta, promoviendo reducir al mínimo los errores de digitación o bien la presentación de DUCAS con datos incorrectos', 44, 1, 1, GETDATE());
INSERT INTO dbo.OEA_Requisito (RequisitoId, SubseccionId, Codigo, Descripcion, Orden, Activo, CreadoPor, CreadoEn) VALUES (45, 9, N'3.5.1', N'El material de envasado, empaque o embalaje de las mercancías es revisado por el responsable de almacén o de seguridad antes de ser utilizado.', 45, 1, 1, GETDATE());
INSERT INTO dbo.OEA_Requisito (RequisitoId, SubseccionId, Codigo, Descripcion, Orden, Activo, CreadoPor, CreadoEn) VALUES (46, 9, N'3.5.2.', N'Debe contar con un procedimiento documentado u otro documento en el que se establezcan las medidas que regulen el manejo y control del material de empaque y embalaje que es recibido, utilizado y desechado.', 46, 1, 1, GETDATE());
INSERT INTO dbo.OEA_Requisito (RequisitoId, SubseccionId, Codigo, Descripcion, Orden, Activo, CreadoPor, CreadoEn) VALUES (47, 10, N'3.6.1', N'Debe contar con un procedimiento documentado u otro documento que deje constancia de la operación del cierre o marchamado al momento del despacho o arribo del medio de transporte, haciendo constar como mínimo lo siguiente (Registro de nombre de los participantes en el proceso, cotejo de número de marchamo con documentación de soporte, control de funcionalidad del marchamo, revisión de que el marchamo no ha sido violentado, entre otras)', 47, 1, 1, GETDATE());
INSERT INTO dbo.OEA_Requisito (RequisitoId, SubseccionId, Codigo, Descripcion, Orden, Activo, CreadoPor, CreadoEn) VALUES (48, 10, N'3.6.2', N'Se debe colocar marchamo o precintos a los medios de transporte de carga tan pronto termine la operación de carga y se efectúa únicamente por las personas autorizadas o por el responsable de seguridad.', 48, 1, 1, GETDATE());
INSERT INTO dbo.OEA_Requisito (RequisitoId, SubseccionId, Codigo, Descripcion, Orden, Activo, CreadoPor, CreadoEn) VALUES (49, 11, N'3.7.1', N'Cuando se trate de Depósitos Aduaneros Temporales debe contar con un sistema de revisión no intrusivo que permita la inspección del equipaje y carga aérea y marítima (Scanner, entre otros).', 49, 1, 1, GETDATE());
INSERT INTO dbo.OEA_Requisito (RequisitoId, SubseccionId, Codigo, Descripcion, Orden, Activo, CreadoPor, CreadoEn) VALUES (50, 12, N'4.1.1', N'Debe contar con un procedimiento documentado u otro documento en el que se establezcan las rutas por las cuales el piloto debe transitar mientras transporta carga de aduana a destino o viceversa y en el que condiciona al piloto a no abandonarla por razones no justificadas.', 50, 1, 1, GETDATE());
INSERT INTO dbo.OEA_Requisito (RequisitoId, SubseccionId, Codigo, Descripcion, Orden, Activo, CreadoPor, CreadoEn) VALUES (51, 12, N'4.1.2', N'Debe contar con un sistema de control del medio de transporte que permita monitorear la unidad en cualquier momento (Dispositivos de control GPS u otros)', 51, 1, 1, GETDATE());
INSERT INTO dbo.OEA_Requisito (RequisitoId, SubseccionId, Codigo, Descripcion, Orden, Activo, CreadoPor, CreadoEn) VALUES (52, 12, N'4.1.3', N'Cuando se contratan servicios de empresas de seguridad para el transporte de mercancías a través de custodios o de Sistemas Globales de Posicionamiento GPS, entre otras, están incluidas cláusulas de seguridad en los contratos', 52, 1, 1, GETDATE());
INSERT INTO dbo.OEA_Requisito (RequisitoId, SubseccionId, Codigo, Descripcion, Orden, Activo, CreadoPor, CreadoEn) VALUES (53, 12, N'4.1.4', N'Debe contar con mecanismos para monitorear tiempos de entregas desde el inicio hasta el final de la ruta establecida y cuando hay retrasos busca enterarse de las causas buscando soluciones o deduciendo responsabilidades.', 53, 1, 1, GETDATE());
INSERT INTO dbo.OEA_Requisito (RequisitoId, SubseccionId, Codigo, Descripcion, Orden, Activo, CreadoPor, CreadoEn) VALUES (54, 12, N'4.1.5', N'Debe contar con un procedimiento documentado o instructivo en el que se establezca que los medios de transporte que se encuentren vacíos o cargados permanezcan cerrados y debidamente controlados', 54, 1, 1, GETDATE());
INSERT INTO dbo.OEA_Requisito (RequisitoId, SubseccionId, Codigo, Descripcion, Orden, Activo, CreadoPor, CreadoEn) VALUES (55, 12, N'4.1.6', N'Debe contar con un procedimiento documentado u otro documento en el cual se establezcan medidas de seguridad del medio de transporte, y verificar, si el transporte es propio o contratado cumpla con las medidas de seguridad establecidas en dicho procedimiento o documento y en el Reglamento de Tránsito vigente (En caso de ser contratado, los requisitos deben quedar establecidos a través de un contrato).', 55, 1, 1, GETDATE());
INSERT INTO dbo.OEA_Requisito (RequisitoId, SubseccionId, Codigo, Descripcion, Orden, Activo, CreadoPor, CreadoEn) VALUES (56, 12, N'4.1.7', N'Debe contar con un mecanismo que permita registrar electrónica o documentalmente las actividades relacionadas con el medio de transporte (Asignación de unidades, nombre del piloto y licencia, registro de mercancías que transporta, lugar de entrega y mercancías entregadas, entre otros)', 56, 1, 1, GETDATE());
INSERT INTO dbo.OEA_Requisito (RequisitoId, SubseccionId, Codigo, Descripcion, Orden, Activo, CreadoPor, CreadoEn) VALUES (57, 13, N'4.2.1', N'Debe contar con un procedimiento documentado u otro documento en el cual se establezcan medidas de seguridad del medio de transporte, el cual incluya las revisiones para la detección de terrorismo, drogas, plagas visibles entre otros.', 57, 1, 1, GETDATE());
INSERT INTO dbo.OEA_Requisito (RequisitoId, SubseccionId, Codigo, Descripcion, Orden, Activo, CreadoPor, CreadoEn) VALUES (58, 13, N'4.2.2', N'Antes de cargar / rellenar / empacar, todos los medios de transporte e instrumentos vacíos de tráfico internacional (IIT, por sus siglas en inglés) deben someterse a inspecciones de seguridad para garantizar que sus estructuras no hayan sido modificadas para ocultar el contrabando o hayan sido contaminadas con plagas visibles.
INSERT INTO dbo.OEA_Requisito (RequisitoId, SubseccionId, Codigo, Descripcion, Orden, Activo, CreadoPor, CreadoEn) VALUES (59, 13, N'4.2.3', N'Para los Transportistas deben incluir inspecciones adicionales en los medios de transporte deben realizarse en los patios de almacenamiento. Siempre que sea posible, se deben realizar inspecciones al entrar y salir de
INSERT INTO dbo.OEA_Requisito (RequisitoId, SubseccionId, Codigo, Descripcion, Orden, Activo, CreadoPor, CreadoEn) VALUES (60, 13, N'4.2.4', N'Debe contar con un procedimiento documentado o instructivo en el que se establezcan medidas para hacer inspecciones al medio de transporte para establecer la existencia de partes y materiales sospechosos ajenos al
INSERT INTO dbo.OEA_Requisito (RequisitoId, SubseccionId, Codigo, Descripcion, Orden, Activo, CreadoPor, CreadoEn) VALUES (61, 13, N'4.2.5', N'Tener procedimiento documentado para evitar la contaminación visible por plagas en conformidad con las reglamentaciones de los Materiales de Embalaje de Madera (WPM). Las medidas visibles de prevención de plagas se deben cumplir en toda la cadena de suministro. Las medidas relacionadas con las reglamentaciones WPM deben cumplir con las Normas. Internacionales para Medidas Fitosanitarias N.º 15 (NIMF 15) de la Convención Internacional de Protección Fitosanitaria (CIPF).', 61, 1, 1, GETDATE());
INSERT INTO dbo.OEA_Requisito (RequisitoId, SubseccionId, Codigo, Descripcion, Orden, Activo, CreadoPor, CreadoEn) VALUES (62, 14, N'4.3.1', N'Debe contar con un programa de mantenimiento preventivo para los medios de transporte (Debe estar debidamente documentado).', 62, 1, 1, GETDATE());
INSERT INTO dbo.OEA_Requisito (RequisitoId, SubseccionId, Codigo, Descripcion, Orden, Activo, CreadoPor, CreadoEn) VALUES (63, 14, N'4.3.2', N'Está registrada en una bitácora cualquier reparación o mantenimiento realizado a los medios de transporte, se debe indicar como mínimo: el nombre del técnico, fecha, hora y descripción de la reparación, entre otros.', 63, 1, 1, GETDATE());
INSERT INTO dbo.OEA_Requisito (RequisitoId, SubseccionId, Codigo, Descripcion, Orden, Activo, CreadoPor, CreadoEn) VALUES (64, 15, N'4.4.1', N'Debe contar con un programa de concientización o capacitación para pilotos sobre amenazas que vulneren la seguridad de la empresa y en las operaciones de carga y descarga. Se deben considerar:
INSERT INTO dbo.OEA_Requisito (RequisitoId, SubseccionId, Codigo, Descripcion, Orden, Activo, CreadoPor, CreadoEn) VALUES (65, 16, N'4.5.1', N'Debe contar con un procedimiento documentado o instructivo en el que se definan las acciones a seguir por el piloto de la unidad de transporte, cuando exista algún imprevisto durante el tránsito de las mercancías (Desperfecto mecánico, robo, accidente, entre otros).', 65, 1, 1, GETDATE());
INSERT INTO dbo.OEA_Requisito (RequisitoId, SubseccionId, Codigo, Descripcion, Orden, Activo, CreadoPor, CreadoEn) VALUES (66, 17, N'5.1.1', N'Debe contar con un procedimiento documentado u otro documento en el cual se regule el ingreso y egreso del personal contratado y subcontratado a la empresa (El procedimiento documentado u otro documento debe indicar la forma de efectuar la identificación de los empleados en los puntos de entrada y salida)', 66, 1, 1, GETDATE());
INSERT INTO dbo.OEA_Requisito (RequisitoId, SubseccionId, Codigo, Descripcion, Orden, Activo, CreadoPor, CreadoEn) VALUES (67, 17, N'5.1.2', N'Debe contar con un procedimiento documentado o instructivo en el cual se regule el ingreso, permanencia y egreso de personal contratado y visitantes. (Debe mantenerse un registro para personal subcontratado y visitantes, presentar identificación y establecer que estas personas solo permanezcan en áreas autorizadas dentro de la empresa). y dirigirse a personas no autorizadas o no identificadas (Intrusos).', 67, 1, 1, GETDATE());
INSERT INTO dbo.OEA_Requisito (RequisitoId, SubseccionId, Codigo, Descripcion, Orden, Activo, CreadoPor, CreadoEn) VALUES (68, 17, N'5.1.3', N'Toda persona y vehículo que ingrese o egrese a las instalaciones de la empresa es revisado por el personal responsable (Tales como pertenencias personales, bolsas o paquetes, cabinas y baúles, entre otras)', 68, 1, 1, GETDATE());
INSERT INTO dbo.OEA_Requisito (RequisitoId, SubseccionId, Codigo, Descripcion, Orden, Activo, CreadoPor, CreadoEn) VALUES (69, 17, N'5.1.4', N'Deber tener medidas para prevenir la introducción y la propagación de organismos dañinos a personas, (cuando corresponda animales y plantas) para minimizar el riesgo de transmisión de enfermedades infecciosas
(ejemplo COVID-19).', 69, 1, 1, GETDATE());
INSERT INTO dbo.OEA_Requisito (RequisitoId, SubseccionId, Codigo, Descripcion, Orden, Activo, CreadoPor, CreadoEn) VALUES (70, 18, N'5.2.1', N'Debe contar con un procedimiento documentado o instructivo que establezca un protocolo que permita la comunicación efectiva entre los empleados, los supervisores o responsables de seguridad; así como con autoridades competentes en caso de producirse una emergencia o siniestro, así como reportar cualquier incidente, actividad sospechosa o personas no autorizadas', 70, 1, 1, GETDATE());
INSERT INTO dbo.OEA_Requisito (RequisitoId, SubseccionId, Codigo, Descripcion, Orden, Activo, CreadoPor, CreadoEn) VALUES (71, 19, N'5.3.1', N'Debe contar con un procedimiento documentado o instructivo que norme el acceso a las áreas críticas de la empresa.', 71, 1, 1, GETDATE());
INSERT INTO dbo.OEA_Requisito (RequisitoId, SubseccionId, Codigo, Descripcion, Orden, Activo, CreadoPor, CreadoEn) VALUES (72, 19, N'5.3.2', N'Debe contar con un plan operativo anual de revisión y mantenimiento de las medidas de seguridad, que incluya la periodicidad, seguimiento específico en áreas críticas, seguridad de sistemas informáticos y mantenimiento de dispositivos de seguridad.', 72, 1, 1, GETDATE());
INSERT INTO dbo.OEA_Requisito (RequisitoId, SubseccionId, Codigo, Descripcion, Orden, Activo, CreadoPor, CreadoEn) VALUES (73, 19, N'5.3.3', N'Los casilleros de los empleados y agentes de seguridad están controlados y ubicados en un área independiente a las áreas críticas.', 73, 1, 1, GETDATE());
INSERT INTO dbo.OEA_Requisito (RequisitoId, SubseccionId, Codigo, Descripcion, Orden, Activo, CreadoPor, CreadoEn) VALUES (74, 19, N'5.3.4', N'Están debidamente señalizadas e identificadas las áreas críticas y el acceso a las mismas es restringido', 74, 1, 1, GETDATE());
INSERT INTO dbo.OEA_Requisito (RequisitoId, SubseccionId, Codigo, Descripcion, Orden, Activo, CreadoPor, CreadoEn) VALUES (75, 19, N'5.3.5', N'Se encuentran las puertas, ventanas, cercas y rejas de las áreas críticas aseguradas con dispositivos de cierre o medidas alternativas de vigilancia o de control de acceso', 75, 1, 1, GETDATE());
INSERT INTO dbo.OEA_Requisito (RequisitoId, SubseccionId, Codigo, Descripcion, Orden, Activo, CreadoPor, CreadoEn) VALUES (76, 20, N'5.4.1', N'Debe contar con un procedimiento documentado o instructivo que incluya medidas de control para el manejo de los dispositivos de cierre y de seguridad (llaves, cerrojos, candados, precintos, caja fuerte, detector de humo, detector de movimiento, mirilla, ofendículos, alambre de púas, razor ribbon, scanner corporal, molinete, sistema de alarmas, detectores de metal, sistemas de revisión no intrusivos, entre otros).', 76, 1, 1, GETDATE());
INSERT INTO dbo.OEA_Requisito (RequisitoId, SubseccionId, Codigo, Descripcion, Orden, Activo, CreadoPor, CreadoEn) VALUES (77, 20, N'5.4.2', N'Debe contar con un procedimiento documentado o instructivo (cronograma) de mantenimiento preventivo y correctivo de los dispositivos de cierre y de seguridad.', 77, 1, 1, GETDATE());
INSERT INTO dbo.OEA_Requisito (RequisitoId, SubseccionId, Codigo, Descripcion, Orden, Activo, CreadoPor, CreadoEn) VALUES (78, 21, N'5.5.1', N'Debe contar con un procedimiento documentado o instructivo en el cual se establezcan medidas de seguridad física de las instalaciones.', 78, 1, 1, GETDATE());
INSERT INTO dbo.OEA_Requisito (RequisitoId, SubseccionId, Codigo, Descripcion, Orden, Activo, CreadoPor, CreadoEn) VALUES (79, 21, N'5.5.2', N'La empresa debe tener un servicio de seguridad competente de conformidad con los requisitos legales y que garantice una acción de respuesta oportuna.', 79, 1, 1, GETDATE());
INSERT INTO dbo.OEA_Requisito (RequisitoId, SubseccionId, Codigo, Descripcion, Orden, Activo, CreadoPor, CreadoEn) VALUES (80, 21, N'5.5.3', N'Debe contar con un procedimiento documentado o instructivo en el que se establezca la forma de enfrentar y dirigirse a personas no autorizadas o no identificadas (Intrusos).', 80, 1, 1, GETDATE());
INSERT INTO dbo.OEA_Requisito (RequisitoId, SubseccionId, Codigo, Descripcion, Orden, Activo, CreadoPor, CreadoEn) VALUES (81, 21, N'5.5.4', N'Debe contar con un sistema de seguridad que garantice la capacidad de respuesta ante amenazas las 24 horas del día.', 81, 1, 1, GETDATE());
INSERT INTO dbo.OEA_Requisito (RequisitoId, SubseccionId, Codigo, Descripcion, Orden, Activo, CreadoPor, CreadoEn) VALUES (82, 21, N'5.5.5', N'Debe contar con planos detallados y actualizados en forma impresa y electrónica en donde se identifique lo siguiente
INSERT INTO dbo.OEA_Requisito (RequisitoId, SubseccionId, Codigo, Descripcion, Orden, Activo, CreadoPor, CreadoEn) VALUES (83, 22, N'5.6.1', N'Se efectúan inspecciones aleatorias y no anunciadas para evaluar el comportamiento y cumplimiento del personal con relación a las medidas de seguridad, dejando registro por escrito de los hallazgos.', 83, 1, 1, GETDATE());
INSERT INTO dbo.OEA_Requisito (RequisitoId, SubseccionId, Codigo, Descripcion, Orden, Activo, CreadoPor, CreadoEn) VALUES (84, 23, N'5.7.1', N'Acorde al actor de la cadena logística y esto sea factible, cuando corresponda debe contar las instalaciones con muros o cercados perimetrales apropiados que permitan el resguardo y seguridad de la empresa.', 84, 1, 1, GETDATE());
INSERT INTO dbo.OEA_Requisito (RequisitoId, SubseccionId, Codigo, Descripcion, Orden, Activo, CreadoPor, CreadoEn) VALUES (85, 23, N'5.7.2', N'En relación con lo expuesto en el numeral anterior, en los casos que corresponda se debe contar con un programa de mantenimiento preventivo y correctivo para preservar la integridad del muro o cercado perimetral.', 85, 1, 1, GETDATE());
INSERT INTO dbo.OEA_Requisito (RequisitoId, SubseccionId, Codigo, Descripcion, Orden, Activo, CreadoPor, CreadoEn) VALUES (86, 24, N'5.8.1', N'Se debería prohibir que los vehículos de visitas o personal se estacionen cerca de las áreas de almacenamiento, manipulación de la carga, de los medios de transportes o en zonas cercanas a los mismos.', 86, 1, 1, GETDATE());
INSERT INTO dbo.OEA_Requisito (RequisitoId, SubseccionId, Codigo, Descripcion, Orden, Activo, CreadoPor, CreadoEn) VALUES (87, 25, N'5.9.1', N'Debe contar con una política que establezca un adecuado manejo de residuos y basura, evitando la acumulación incontrolable en las zonas críticas, con el propósito de contrarrestar ilícitos y riesgos para la empresa.', 87, 1, 1, GETDATE());
INSERT INTO dbo.OEA_Requisito (RequisitoId, SubseccionId, Codigo, Descripcion, Orden, Activo, CreadoPor, CreadoEn) VALUES (88, 25, N'5.9.2', N'Los depósitos recolectores generales de basura están ubicados en zonas de difícil acceso al personal, poco transitadas y en lo posible retirados de las áreas donde se encuentren las materias primas o mercancías.', 88, 1, 1, GETDATE());
INSERT INTO dbo.OEA_Requisito (RequisitoId, SubseccionId, Codigo, Descripcion, Orden, Activo, CreadoPor, CreadoEn) VALUES (89, 25, N'5.9.3', N'Debe contar con una programación de inspección física de los basureros antes de ser recolectados y retirados, ya sea por vehículos o medios utilizados por la empresa.', 89, 1, 1, GETDATE());
INSERT INTO dbo.OEA_Requisito (RequisitoId, SubseccionId, Codigo, Descripcion, Orden, Activo, CreadoPor, CreadoEn) VALUES (90, 17, N'5.10.1', N'Dispone de circuito cerrado de televisión CCTV que monitoree las áreas críticas con enlace al Servicio Aduanero de Guatemala.', 90, 1, 1, GETDATE());
INSERT INTO dbo.OEA_Requisito (RequisitoId, SubseccionId, Codigo, Descripcion, Orden, Activo, CreadoPor, CreadoEn) VALUES (91, 17, N'5.10.2', N'El circuito cerrado de televisión CCTV, debe estar auxiliado por un sistema de generación eléctrica o medio de respaldo como UPS.', 91, 1, 1, GETDATE());
INSERT INTO dbo.OEA_Requisito (RequisitoId, SubseccionId, Codigo, Descripcion, Orden, Activo, CreadoPor, CreadoEn) VALUES (92, 17, N'5.10.3', N'La empresa debe contar con Sistemas de respaldo de imágenes y video (grabación) con la capacidad de almacenamiento de por lo menos dos meses para responder a posibles eventos.', 92, 1, 1, GETDATE());
INSERT INTO dbo.OEA_Requisito (RequisitoId, SubseccionId, Codigo, Descripcion, Orden, Activo, CreadoPor, CreadoEn) VALUES (93, 26, N'6.1.1', N'Debe contar con un procedimiento documentado o instructivo en el cual se establezcan medidas para la seguridad industrial', 93, 1, 1, GETDATE());
INSERT INTO dbo.OEA_Requisito (RequisitoId, SubseccionId, Codigo, Descripcion, Orden, Activo, CreadoPor, CreadoEn) VALUES (94, 26, N'6.1.2', N'Debe contar con dictamen técnico en donde se haga constar que las medidas de seguridad industrial cumplen, como mínimo, con los estándares siguientes
INSERT INTO dbo.OEA_Requisito (RequisitoId, SubseccionId, Codigo, Descripcion, Orden, Activo, CreadoPor, CreadoEn) VALUES (95, 27, N'6.2.1', N'Debe contar con un procedimiento documentado o instructivo que detalle la planificación, programación y forma de efectuar simulacros de evacuación, para los casos de amenazas que vulneren la seguridad', 95, 1, 1, GETDATE());
INSERT INTO dbo.OEA_Requisito (RequisitoId, SubseccionId, Codigo, Descripcion, Orden, Activo, CreadoPor, CreadoEn) VALUES (96, 27, N'6.2.2', N'Debe contar con un plan de respuesta empresarial o institucional ante emergencias y contingencias que pueden afectar la seguridad.', 96, 1, 1, GETDATE());
INSERT INTO dbo.OEA_Requisito (RequisitoId, SubseccionId, Codigo, Descripcion, Orden, Activo, CreadoPor, CreadoEn) VALUES (97, 27, N'6.2.3', N'Debe contar con un programa de ejercicios de escritorio o de simulacros de acuerdo con los riesgos que haya contemplado en su plan de respuesta empresarial o institucional ante emergencias y contingencias que vulneren la seguridad.', 97, 1, 1, GETDATE());
INSERT INTO dbo.OEA_Requisito (RequisitoId, SubseccionId, Codigo, Descripcion, Orden, Activo, CreadoPor, CreadoEn) VALUES (98, 28, N'6.3.1', N'Debe contar con un sistema de iluminación adecuada dentro y fuera de las instalaciones, especialmente en las siguientes áreas críticas, muros perimetrales y estacionamientos.', 98, 1, 1, GETDATE());
INSERT INTO dbo.OEA_Requisito (RequisitoId, SubseccionId, Codigo, Descripcion, Orden, Activo, CreadoPor, CreadoEn) VALUES (99, 28, N'6.3.2', N'Debe contar con un programa de mantenimiento preventivo y correctivo para el sistema de iluminación.', 99, 1, 1, GETDATE());
INSERT INTO dbo.OEA_Requisito (RequisitoId, SubseccionId, Codigo, Descripcion, Orden, Activo, CreadoPor, CreadoEn) VALUES (100, 28, N'6.3.3', N'Debe contar con un sistema de iluminación de emergencia para las áreas críticas.', 100, 1, 1, GETDATE());
INSERT INTO dbo.OEA_Requisito (RequisitoId, SubseccionId, Codigo, Descripcion, Orden, Activo, CreadoPor, CreadoEn) VALUES (101, 28, N'6.3.4', N'Dispone de un generador de energía eléctrica (planta o generador eléctrico entre otros).', 101, 1, 1, GETDATE());
INSERT INTO dbo.OEA_Requisito (RequisitoId, SubseccionId, Codigo, Descripcion, Orden, Activo, CreadoPor, CreadoEn) VALUES (102, 28, N'6.3.5', N'Debe contar con un programa de mantenimiento para el generador de energía que incluya un plan de contingencia ante un eventual fallo de este.', 102, 1, 1, GETDATE());
INSERT INTO dbo.OEA_Requisito (RequisitoId, SubseccionId, Codigo, Descripcion, Orden, Activo, CreadoPor, CreadoEn) VALUES (103, 29, N'6.4.1', N'Debe contar con un sistema de  ventilación adecuado en cada área  de trabajo según el tipo de  actividad que se realice', 103, 1, 1, GETDATE());
INSERT INTO dbo.OEA_Requisito (RequisitoId, SubseccionId, Codigo, Descripcion, Orden, Activo, CreadoPor, CreadoEn) VALUES (104, 30, N'7.1', N'Debe contar con un procedimiento documentado u otro documento en el cual se establezcan medidas para la seguridad de la información, el sistema Informático y la ciberseguridad que sean integrales que incluyan proteger los sistemas de tecnología de la información, como mínimo, todos los criterios individuales de la ciberseguridad.', 104, 1, 1, GETDATE());
INSERT INTO dbo.OEA_Requisito (RequisitoId, SubseccionId, Codigo, Descripcion, Orden, Activo, CreadoPor, CreadoEn) VALUES (105, 30, N'7.2', N'Existe dentro de la estructura organizacional un responsable de velar por la seguridad física del área en la que se resguarda la información y del funcionamiento del sistema informático', 105, 1, 1, GETDATE());
INSERT INTO dbo.OEA_Requisito (RequisitoId, SubseccionId, Codigo, Descripcion, Orden, Activo, CreadoPor, CreadoEn) VALUES (106, 30, N'7.3', N'Debe contar con un procedimiento documentado u otro documento en el que se establezcan medidas en vigencia para la protección de la información con carácter confidencial. Debe contar con un sistema de clasificación de información en el que se establezcan los niveles de importancia de esta (Confidencial, reservada y pública).', 106, 1, 1, GETDATE());
INSERT INTO dbo.OEA_Requisito (RequisitoId, SubseccionId, Codigo, Descripcion, Orden, Activo, CreadoPor, CreadoEn) VALUES (107, 30, N'7.4', N'Debe contar con un control de inventarios de los activos que contienen la información.', 107, 1, 1, GETDATE());
INSERT INTO dbo.OEA_Requisito (RequisitoId, SubseccionId, Codigo, Descripcion, Orden, Activo, CreadoPor, CreadoEn) VALUES (108, 30, N'7.5', N'Maneja bitácoras confiables que permitan una adecuada trazabilidad de las operaciones registradas en el sistema.', 108, 1, 1, GETDATE());
INSERT INTO dbo.OEA_Requisito (RequisitoId, SubseccionId, Codigo, Descripcion, Orden, Activo, CreadoPor, CreadoEn) VALUES (109, 30, N'7.6', N'Para defender los sistemas de Tecnología de la Información (TI) de amenazas de ciberseguridad comunes, una empresa debe instalar suficientes programas de software y equipos para protegerse de programas malignos (virus, programas espías, gusanos y troyanos, etc.) y de intrusiones externas e internas (cortafuegos) en los sistemas de cómputo de la empresa. Los actores de la cadena logística deben asegurarse de que su software de seguridad esté actualizado y reciba actualizaciones de seguridad periódicas. Los OEA-GT deben contar con políticas y procedimientos para prevenir ataques a través de la ingeniería social. Si se da una filtración de datos u otro evento imprevisto provoca la pérdida de datos o equipo, los procedimientos deben incluir una recuperación (o reemplazo) de los sistemas o datos de IT.', 109, 1, 1, GETDATE());
INSERT INTO dbo.OEA_Requisito (RequisitoId, SubseccionId, Codigo, Descripcion, Orden, Activo, CreadoPor, CreadoEn) VALUES (110, 30, N'7.7', N'Debe contar con un procedimiento en el que se establezca que deben de realizarse copias de seguridad para que exista un resguardo histórico de la información (Back Up con copias fuera de las instalaciones de la empresa debidamente etiquetados). Se debe realizar una copia de seguridad de los datos una vez por semana o según sea apropiado. Todos los datos confidenciales y sensibles se deberían almacenar en formato cifrado.', 110, 1, 1, GETDATE());
INSERT INTO dbo.OEA_Requisito (RequisitoId, SubseccionId, Codigo, Descripcion, Orden, Activo, CreadoPor, CreadoEn) VALUES (111, 30, N'7.8', N'Los actores de la cadena logística que utilizan sistemas de redes deben evaluar periódicamente la seguridad de su infraestructura de IT. Si se encuentran vulnerabilidades, las acciones correctivas deben implementarse tan pronto como sea posible. Debe contar con controles de seguridad para accesos a red (Firewalls, manejo de direcciones, servidores de autenticación, entre otros).', 111, 1, 1, GETDATE());
INSERT INTO dbo.OEA_Requisito (RequisitoId, SubseccionId, Codigo, Descripcion, Orden, Activo, CreadoPor, CreadoEn) VALUES (112, 30, N'7.9', N'Tiene establecidos requisitos de seguridad que deben cumplir los socios comerciales que tienen acceso a su sistema informático. Las políticas de ciberseguridad deberían abordar cómo un miembro comparte información sobre amenazas de ciberseguridad con el gobierno y otros socios comerciales.', 112, 1, 1, GETDATE());
INSERT INTO dbo.OEA_Requisito (RequisitoId, SubseccionId, Codigo, Descripcion, Orden, Activo, CreadoPor, CreadoEn) VALUES (113, 30, N'7.1', N'Las políticas y los procedimientos de seguridad cibernética deben incluir medidas para evitar el uso de productos tecnológicos falsificados o con licencias inapropiadas.
INSERT INTO dbo.OEA_Requisito (RequisitoId, SubseccionId, Codigo, Descripcion, Orden, Activo, CreadoPor, CreadoEn) VALUES (114, 30, N'7.11', N'Debe contar con políticas de seguridad en las que se definan las responsabilidades de los usuarios y controles de accesos. El acceso del usuario debe restringirse según la descripción del trabajo o las tareas asignadas.
INSERT INTO dbo.OEA_Requisito (RequisitoId, SubseccionId, Codigo, Descripcion, Orden, Activo, CreadoPor, CreadoEn) VALUES (115, 30, N'7.12', N'Tiene desarrollado un plan de contingencia que contemple medidas aplicables en caso de producirse fallas en el sistema.', 115, 1, 1, GETDATE());
INSERT INTO dbo.OEA_Requisito (RequisitoId, SubseccionId, Codigo, Descripcion, Orden, Activo, CreadoPor, CreadoEn) VALUES (116, 30, N'7.13', N'Tiene una política definida para la protección de los derechos de propiedad intelectual y de autor de los sistemas operativos y software.', 116, 1, 1, GETDATE());
INSERT INTO dbo.OEA_Requisito (RequisitoId, SubseccionId, Codigo, Descripcion, Orden, Activo, CreadoPor, CreadoEn) VALUES (117, 30, N'7.14', N'Las políticas y los procedimientos de ciberseguridad se deben revisar anualmente, o con mayor frecuencia, según lo establezcan el riesgo o las circunstancias. Después de la revisión, las políticas y los procedimientos se deben actualizar, en caso de ser necesario.', 117, 1, 1, GETDATE());
INSERT INTO dbo.OEA_Requisito (RequisitoId, SubseccionId, Codigo, Descripcion, Orden, Activo, CreadoPor, CreadoEn) VALUES (118, 30, N'7.15', N'El servidor informático o cualquier otro dispositivo de almacenamiento masivo de datos electrónicos que contenga información se resguarda en un ambiente que garantice la seguridad del equipo y la información que contiene:
INSERT INTO dbo.OEA_Requisito (RequisitoId, SubseccionId, Codigo, Descripcion, Orden, Activo, CreadoPor, CreadoEn) VALUES (119, 30, N'7.16', N'En los casos que corresponda acorde al actor de la cadena logística de que se trate, las declaraciones de mercancías que se transmitan al servidor de la SAT son capturadas y transmitidas desde las computadoras ubicadas en las oficinas registradas ante la SAT.', 119, 1, 1, GETDATE());
INSERT INTO dbo.OEA_Requisito (RequisitoId, SubseccionId, Codigo, Descripcion, Orden, Activo, CreadoPor, CreadoEn) VALUES (120, 30, N'7.17', N'En los casos que corresponda acorde al actor de la cadena logística de que se trate se debe contar con certificados digitales instalados en las computadoras desde las cuales capturan y transmiten las declaraciones aduaneras al servidor de la SAT.', 120, 1, 1, GETDATE());
INSERT INTO dbo.OEA_Requisito (RequisitoId, SubseccionId, Codigo, Descripcion, Orden, Activo, CreadoPor, CreadoEn) VALUES (121, 30, N'7.18', N'Debe contar con controles adecuados para el mantenimiento o servicio del equipo informático.', 121, 1, 1, GETDATE());
INSERT INTO dbo.OEA_Requisito (RequisitoId, SubseccionId, Codigo, Descripcion, Orden, Activo, CreadoPor, CreadoEn) VALUES (122, 30, N'7.19', N'Los actores de la cadena logística que permiten que sus usuarios se conecten de forma remota a una red deben emplear tecnologías seguras, como redes privadas virtuales (VPN) para permitir que los empleados accedan a la intranet de la empresa de forma segura cuando se encuentran fuera de la oficina. Los miembros también deben tener procedimientos diseñados para evitar el acceso remoto de usuarios no autorizados.', 122, 1, 1, GETDATE());
INSERT INTO dbo.OEA_Requisito (RequisitoId, SubseccionId, Codigo, Descripcion, Orden, Activo, CreadoPor, CreadoEn) VALUES (123, 30, N'7.2', N'Debe existir un sistema para identificar el acceso no autorizado a los sistemas o datos de IT o el abuso de políticas y procedimientos, incluido el acceso inadecuado a los sistemas internos o sitios web externos y la manipulación o alteración de los datos comerciales por parte de los empleados o contratistas. Todos los infractores deben estar sujetos a las acciones disciplinarias correspondientes.', 123, 1, 1, GETDATE());
INSERT INTO dbo.OEA_Requisito (RequisitoId, SubseccionId, Codigo, Descripcion, Orden, Activo, CreadoPor, CreadoEn) VALUES (124, 31, N'8.1', N'Debe contar con un procedimiento documentado o instructivo en el que se haga constar que los precintos o marchamos que se compran para asegurar la carga cumplen con la Norma ISO 17712 vigente.', 124, 1, 1, GETDATE());
INSERT INTO dbo.OEA_Requisito (RequisitoId, SubseccionId, Codigo, Descripcion, Orden, Activo, CreadoPor, CreadoEn) VALUES (125, 31, N'8.2', N'Debe contar con un procedimiento documentado o instructivo que incluya medidas de control de los sellos de seguridad, marchamos o precintos desde la adquisición, la utilización y el desecho de estos.
INSERT INTO dbo.OEA_Requisito (RequisitoId, SubseccionId, Codigo, Descripcion, Orden, Activo, CreadoPor, CreadoEn) VALUES (126, 31, N'8.3', N'El responsable o supervisor asignado al control de sellos de seguridad, marchamos o precintos deja un registro debidamente documentado de entrega y recepción de los mismos.', 126, 1, 1, GETDATE());
INSERT INTO dbo.OEA_Requisito (RequisitoId, SubseccionId, Codigo, Descripcion, Orden, Activo, CreadoPor, CreadoEn) VALUES (127, 31, N'8.4', N'El responsable o supervisor asignado al control de sellos de seguridad, marchamos o precintos deja un registro debidamente documentado de entrega y recepción de estos.', 127, 1, 1, GETDATE());
INSERT INTO dbo.OEA_Requisito (RequisitoId, SubseccionId, Codigo, Descripcion, Orden, Activo, CreadoPor, CreadoEn) VALUES (128, 31, N'8.5', N'Debe contar con un procedimiento que incluya, entre otras, las normas de tratamiento de registro incorrecto de la numeración, el desecho de los sellos de seguridad, marchamos o precintos que fueron utilizados o inutilizados.', 128, 1, 1, GETDATE());
INSERT INTO dbo.OEA_Requisito (RequisitoId, SubseccionId, Codigo, Descripcion, Orden, Activo, CreadoPor, CreadoEn) VALUES (129, 31, N'8.6', N'Debe contar con un procedimiento que incluya las actuaciones a seguir en el caso detectar indicios de manipulación ilícita de sellos de seguridad, marchamos o precintos.', 129, 1, 1, GETDATE());
INSERT INTO dbo.OEA_Requisito (RequisitoId, SubseccionId, Codigo, Descripcion, Orden, Activo, CreadoPor, CreadoEn) VALUES (130, 31, N'8.7', N'Debe contar con control de inventarios de mínimos y máximos de marchamos, precintos o sellos de seguridad.', 130, 1, 1, GETDATE());
INSERT INTO dbo.OEA_Requisito (RequisitoId, SubseccionId, Codigo, Descripcion, Orden, Activo, CreadoPor, CreadoEn) VALUES (131, 31, N'8.8', N'Los sellos de seguridad, marchamos o precintos están resguardados en un ambiente cerrado y seguro con acceso sólo a personal autorizado.', 131, 1, 1, GETDATE());
INSERT INTO dbo.OEA_Requisito (RequisitoId, SubseccionId, Codigo, Descripcion, Orden, Activo, CreadoPor, CreadoEn) VALUES (132, 31, N'8.9', N'Debe contar con un procedimiento documentado o instructivo para reconocer y reportar sellos, marchamos o precintos comprometidos, entre otros.', 132, 1, 1, GETDATE());
